import React from 'react';
import {View, Text, StyleSheet} from 'react-native';
import{
    DrawerContentScrollView,
    DrawerItem
} from '@react-navigation/drawer';
import {
    Avatar,
    Title,
    Caption,
    Paragraph,
    Drawer,
    TouchableRipple,
    Switch
} from 'react-native-paper';
import { MaterialCommunityIcons } from 'react-native-vector-icons/MaterialCommunityIcons';
import Feather from 'react-native-vector-icons/Feather';


export function DrawerContent(props){
    return(
        <View style={{flex:1, backgroundColor:'#fff'}}>
        <DrawerContentScrollView {...props}>
        <View style={styles.drawerContent}>
        <View style ={styles.userInfoSection}>
        <View>
        <Avatar.Image
        source={require("../assets/propic.jpg")}
        size={80}
        />
        <Title style={styles.title}>Biya Girma</Title>
        </View>
        </View>
        </View>
        <Drawer.Section style={styles.drawerSection}>
        <Drawer.Section/>
        <DrawerItem
           icon={({color, size}) => (
                <Feather 
                    name="home" 
                    color={'#a7b5d0'}
                     size={20}
                />
                            )}
            label="Home"
            onPress={() => {}}
        />
        <DrawerItem
           icon={({color, size}) => (
                <Feather 
                    name="user" 
                    color={'#a7b5d0'}
                     size={20}
                />
                            )}
            label="Profile"
            onPress={() => {}}
        />
        <DrawerItem
           icon={({color, size}) => (
                <Feather 
                    name="help-circle" 
                    color={'#a7b5d0'}
                     size={20}
                />
                            )}
            label="Help"
            onPress={() => {}}
        />
         <DrawerItem
           icon={({color, size}) => (
                <Feather 
                    name="settings" 
                    color={'#a7b5d0'}
                     size={20}
                />
                            )}
            label="Settings"
            onPress={() => {}}
        />
        <Drawer.Section title="Preferences"/>
        <TouchableRipple>
        <View style={styles.preference}>
        <Text style={{color:'grey'}}>Dark Theme</Text>
        <Switch/>

        </View>
        </TouchableRipple>
        </Drawer.Section>

        </DrawerContentScrollView>
        <Drawer.Section style={styles.bottomDrawerSection}>
        <DrawerItem
         icon={({color, size}) => (
                <Feather 
                    name="log-out" 
                    color={'#a7b5d0'}
                    size={20}
                />
                            )}
            label="Sign out"
            onPress={() => {}}
            />
        </Drawer.Section>
        </View>
    );
}

const styles = StyleSheet.create({
    drawerContent: {
      flex: 1,      
    },
    userInfoSection: {
      paddingTop:3,
      justifyContent:'center',
      alignItems:'center',

    },
    title: {
      fontSize:17,
      marginTop: 10,
      alignItems:'center',
      color:'#000'
    },
    section: {
      flexDirection: 'row',
      alignItems: 'center',
      marginRight: 15,
    },
    paragraph: {
      fontWeight: 'bold',
      marginRight: 3,
    },
    drawerSection: {
      marginTop: 1,

    },
    bottomDrawerSection: {
        marginBottom: 5,
        borderTopColor: '#f4f4f4',
        borderTopWidth: 1
    },
    preference: {
      flexDirection: 'row',
      justifyContent: 'space-between',
      paddingVertical: 5,
      paddingHorizontal: 16,
    },
  });
